// eslint-disable-next-line import/no-anonymous-default-export
import shortid from "shortid"

export function createNotesStore(){
    return{
        notes: [],
        create(title){
            this.notes.push({
                id: shortid.generate(),
                title,
                isOver: false
            })
        },
        deleteGoods(id){
            this.notes = this.notes.filter(item => item.id !== id)
        }
    }
}
