import React from 'react';
import { Container, Form, FloatingLabel, Button} from 'react-bootstrap';
import { observer, useLocalObservable } from "mobx-react-lite";
import GoodsStore from '../store/GoodsStore';

export const GoodsForm = observer(() => {
    const itemStore = useLocalObservable(() => (GoodsStore));


    return (
        <Container>
                <FloatingLabel
                    controlId="floatingInput"
                    label="Enter product name"
                    className="mb-3"
                >
                    <Form.Control type="text" placeholder="Enter product name" />
                </FloatingLabel>
                <FloatingLabel 
                    controlId="floatingQuantity" 
                    label="Enter the quantity of goods"
                    className="mb-3"
                >
                    <Form.Control type="number" placeholder="Enter the quantity of goods" />
                </FloatingLabel>
                <Button
                    variant="warning" 
                    size="lg"
                    onClick={itemStore.create}
                >
                    Confirm
                </Button>
        </Container>
    )
})
