// eslint-disable-next-line import/no-anonymous-default-export
import shortid from "shortid"

export function createGoodsStore(){
    return{
        goods: [],
        create(title, quantity){
            this.goods.push({
                id: shortid.generate(),
                title,
                quantity,
                isOver: false
            })
        },
        deleteGoods(id){
            this.goods = this.goods.filter(item => item.id !== id)
        }
    }
}
