import { useLocalStore } from 'mobx-react-lite';
import React from 'react';
import { createGoodsStore } from '../store/GoodsStore';

const GoodsContext = React.createContext(null)


export const GoodsProvider = ({children}) => {
    const goodsStore = useLocalStore(createGoodsStore)

    return <GoodsContext.Provider value={goodsStore}>
        {children}
    </GoodsContext.Provider>
}

export const useGoodsStore = () => React.useContext(GoodsContext)
